var currentTime = msg.payload;
var lastTickTime = global.get("lastTickTime") || currentTime;
var timeRemaining = global.get("timeRemaining") || 0;
var gameState = global.get("gameState");

if(gameState == "playing") {
    timeRemaining -= (currentTime - lastTickTime);
    global.set("timeRemaining", timeRemaining);
}

msg.timeRemaining = timeRemaining;
global.set("lastTickTime", currentTime);

node.status({fill:"green", shape:"dot", text:timeRemaining});

return msg;

// msg.time is in milliseconds
var t = msg.timeRemaining / 1000;
var h = Math.floor(t / 3600);
var m = Math.floor(t % 3600 / 60);
var s = Math.floor(t % 3600 % 60);

// Format into hh:mm:ss
msg.timerText = ("0" + h).slice(-2) + ":" + ("0" + m).slice(-2) + ":" + ("0" + s).slice(-2);

// Update the editor node
node.status({fill:"green", shape:"dot", text:msg.timerText});

// Forward the message along the flow
return msg;
